export function lookup(method, endpoint, callback, data) {
  let jsonData = {};

  if (data) {
    jsonData = JSON.stringify(data);
  }

  const xhr = new XMLHttpRequest();
  const url = `http://localhost:8000/api${endpoint}`;
  xhr.responseType = "json";
  xhr.open(method, url);

  xhr.onload = () => {
    callback(xhr.response, xhr.status);
  };

  xhr.onerror = () => {
    alert("Sorry, a serious error occured");
  };

  xhr.send(jsonData);
}
export function displayTweets(callback) {
  lookup('GET', "/tweets/", callback);
}
export function createTweet(callback, tweetValue) {
  lookup('GET', "/tweets/create/", callback, {
    content: tweetValue
  });
