import React, {useState, useEffect} from "react"
import logo from './logo.svg';
import './App.css';

function displayTweets(callback){
            const xhr = new XMLHttpRequest()
            const method = "GET"
            const url = "/tweets"
            const responseType = "json"

            xhr.responseType = responseType
            xhr.open(method, url)

            xhr.onload = () => {
              callback(response, status)
            }

            xhr.onerror = () => {
                alert("Sorry, a serious error occured");
            }
            xhr.send()
        }

function App() {
  const [tweets, setTweets] = useState([])

  useEffect(() => {
    const setTweetsCallback = (response, status) => {
      if (status === 200){
      setTweets(response)}
      }
    displayTweets(setTweetsCallback)

  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
      <div>
          {
          tweets.map((singleTweet, index) =>{
            const {id, content, likes} = singleTweet
            return(
              <li>
                <h3>{singleTweet.content}</h3>
              </li>
            )
          })
          }
      </div>
    </div>
  );
}

export default App;
