import React, {useState, useEffect} from "react"
import logo from './logo.svg';
import './App.css';

const ActionBtn = (props) => {
  const {singleTweet, action} = props
  const btnClass = action === "like" ? "btn-primary" : action === "unlike" ? "btn-secondary" : "btn-outline-secondary"
  const btnText = {like: "likes", unlike: "unlike", retweet: "retweet"}
  return(
    <button className="btn btn-primary">{props.likes}</button>
  )
}

const Tweet = (props) => {
  const {content, likes} = props.singleTweet
  return (
    <div className="col-md-6 col-10 py-5 my-3 border-bottom">
      <h3>{content}</h3>
    </div>
  )
}

function displayTweets(callback){
            const xhr = new XMLHttpRequest()
            const method = "GET"
            const url = "http://localhost:8000/api/tweets/"
            const responseType = "json"

            xhr.responseType = responseType
            xhr.open(method, url)

            xhr.onload = () => {
              callback(xhr.response, xhr.status)
            }

            xhr.onerror = () => {
                alert("Sorry, a serious error occured");
            }
            xhr.send()
        }

function App() {
  const [tweets, setTweets] = useState([])

  useEffect(() => {
    const setTweetsCallback = (response, status) => {
      if (status === 200){
        setTweets(response)
        if (!tweets) {
          setTweets([{id: 1, content: "hey"}, {id: 2, content: "heysup"}, {id: 3, content: "sup"}])      
        }

      }
    }
    displayTweets(setTweetsCallback)

  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
      <div className="container align-center">
          {
          tweets.map((singleTweet, index) =>{
            const {id, content, likes} = singleTweet
            return(
              <Tweet key={`${index}-${id}`} singleTweet = {singleTweet} />
            )
          })
          }
      </div>
    </div>
  );
}

export default App;
